
public class HelloSccc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		String b ="ssss";
		addd(a);
		adddc(b);
		
		System.out.println(a+"   "+b);
		
		
		
		
		
	}
	
	public static void addd(int x){
		x++;
		
	}
	
	public static void adddc(String x){
		x=x+x;
		System.out.println(x);
	}
}
